const request = require('supertest');
const expect = require('chai').expect;
const server = require('./lib/express');

describe('Parse Expression', function () {

  const expression = '2d20k1+5';

  it('base', function (done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'parse_expression',
          moduleParam: {
            expression: expression
          },
          registrationData: {
          }
        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function (err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        expect(res.body.value).to.have.property('expression').to.equal(expression);
        expect(res.body.value).to.have.property('expressions');
        expect(res.body.value).to.have.property('result');
        done();
      });
  })
});
